import SwiftUI

struct CharView: View {
    var personagemSelecionado : RiMo
    
    var body: some View {
        ZStack{
            AsyncImage(url: URL(string: "data:image/jpeg;base64,"),
                       content: { image in
                                           image.resizable()
                                              
                                       },
                                       placeholder: {
                                           ProgressView()
                                       }).ignoresSafeArea()
                .aspectRatio(contentMode: .fill)
                .blur(radius: 10)

            
            VStack{
                AsyncImage(url: URL(string:personagemSelecionado.image!),
                           content: { image in
                    image.resizable()
                        .scaledToFill()
                },
                           placeholder: {
                    ProgressView()
                }).frame(width: 250, height: 250)
                    .background(Color.gray)
                    .clipShape(Circle())
                    .padding(10)
                
            }
        }
    }
}


